export const SuccessIcon = ({
  animated,
  ...props
}: SVGProps & { animated?: boolean }) => {
  const circleRef = useRef(null);
  const lineRef = useRef(null);

  useEffect(() => {
    const animate = () => {
      gsap.fromTo(
        circleRef.current,
        {
          strokeDasharray: 661,
          strokeDashoffset: 551,
        },
        {
          strokeDashoffset: 0,
          duration: 1.2,
          ease: 'power2.inOut',
        },
      );

      gsap.fromTo(
        lineRef.current,
        {
          strokeDasharray: 138,
          strokeDashoffset: 138,
        },
        {
          strokeDashoffset: 0,
          delay: 0.7,
          duration: 0.7,
          ease: 'power2.inOut',
        },
      );
    };

    if (animated) animate();
  }, []);

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      width="247"
      height="246"
      viewBox="0 0 247 246"
      fill="none"
      {...props}
    >
      <circle
        cx="123.5"
        cy="123"
        r="105.125"
        stroke="#0A1ED9"
        strokeWidth="5"
        ref={circleRef}
      />
      <path
        d="M77.375 115.312L112.856 153.75L169.625 92.25"
        ref={lineRef}
        stroke="#0A1ED9"
        strokeWidth="5"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
};

